INSERT INTO `messaging_message` (`id`,`name`,`subject`,`body`,`sends`,`message_timing`,`x_value`,`recipient_function`,`send_as_batch`,`batch_window`,`time_snap`,`minimum_duration`,`created`,`updated`)
VALUES
	(6,'Registration','Welcome to Repower at Home','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Welcome {{ content_object.first_name }}!\r\n</p>\r\n<p>\r\n    Congratulations on your new Repower at Home account. You’ve taken your first step on the path\r\n    towards a smarter home and a cleaner world.\r\n</p>\r\n<p>\r\n    At Repower at Home, cool cats like you are getting together to save energy, save money, \r\n    and save the world.\r\n</p>\r\n<p>\r\n    Can’t wait to get started? <a href=\"http://{{ domain }}{%% url login %%}\">Click here to log in</a>.\r\n</p>\r\n<p>\r\n    With so much to do, where should you begin?  Here are some bright ideas:</p>\r\n    <ul>\r\n        <li>Jump in and complete this month’s <a href=\"http://{{ domain }}{%% url index %%}\">featured action</a>.</li>\r\n        <li>Get plugged in to your local energy efficiency scene by <a href=\"http://{{ domain }}{%% url group_list %%}\">\r\n            finding a team to join</a>.</li> \r\n        <li>Are you the first to arrive at the party? \r\n            <a href=\"http://{{ domain }}{%% url group_create %%}\">Start a team</a> for your \r\n            neighborhood, school, church, or knitting club. Then, organize your team to take \r\n            action together.</li> \r\n        <li>Spread the word! <a href=\"http://twitter.com/home?status=I\'m%%20saving%%20money%%20and%%20having%%20fun%%20with%%20%%40repowerathome.%%20Check%%20out%%20http%%3A%%2F%%2Frepowerathome.com%%2F\">Tweet it out</a>, \r\n            <a href=\"http://www.facebook.com/sharer.php?u=http%%3A%%2F%%2Frepowerathome.com%%2F\">share on Facebook</a>, send some smoke \r\n            signals, whatever. Let everyone know about all the good you’re doing, and how they \r\n            can join in on the fun.</li>\r\n</ul>\r\n<p>\r\n    As you cruise around the site, let us know what you think by dropping us a line at feedback@repowerathome.com.\r\n</p>\r\n<p>If you are interested in starting a team or hosting an energy meeting or just want to learn more contact Brian Ward at brian.ward@climateprotect.org or call (202) 567-6800.</p><p>Out next Repower at Home Introduction call will be on September 15th at 9:00pm EST.  On this call you will learn more about Repower at Home and how you can be part of the movement. <a href=\'http://bit.ly/clk2Kz\'>Click here to RSVP</a>.</p>\r\n<p>\r\n    Welcome home,<br /> \r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'email',0,NULL,NULL,NULL,'2010-08-19 11:00:22','2010-08-19 15:53:44'),
	(7,'New Account','New RAH User: {{ content_object.email }}','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\nThe following user has just registered for a RAH account:\r\n    <a href=\"http://{{ domain }}{{ content_object.get_absolute_url }}\">{{ content_object.get_full_name }}</a>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: \"newaccounts@repowerathome.com\"',0,NULL,NULL,NULL,'2010-08-19 12:01:47','2010-08-19 15:54:10'),
	(8,'New Team Discussion','Repower at Home {{ content_object.group }} Discussion - {{ content_object.subject }}','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi {{ recipient.get_full_name }},<br />\r\n    {{ content_object.user.get_full_name }} has posted a new discussion, {{ content_object.subject }}, on your {{ content_object.group }}\'s team page. \r\n</p>\r\n<h2>{{ content_object.subject }}</h2>\r\n{{ content_object.body|linebreaks }}\r\n<p>\r\n    <a href=\"http://{{ domain }}{{ content_object.get_absolute_url }}\">Click here to check it out</a> \r\n</p>\r\n<p>\r\n    Thanks,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: [u for u in x.group.users_not_blacklisted() if u.pk != x.user.pk]',0,NULL,NULL,NULL,'2010-08-20 15:48:01','2010-08-20 16:01:29'),
	(9,'Team Join Request','Team Join Request','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi all,<br />\r\n    Your team, {{ content_object.group }}, is all the rage right now. {{ content_object.user.get_full_name }} <br />\r\n    ({{ content_object.user.email }}) just requested to join.\r\n</p>\r\n<p>\r\n    Click here to <a href=\"http://{{ domain }}{%% url group_approve content_object.group.id content_object.user.id %%}\">accept</a> \r\n    or <a href=\"http://{{ domain }}{%% url group_deny content_object.group.id content_object.user.id %%}\">deny</a> the request\r\n</p>\r\n<p>\r\n    Thanks for steering your team towards a more efficient future!\r\n</p>\r\n<p>\r\n    Remember, {{ content_object.group }} is a closed team. Anyone who wants to join the <br />\r\n    team has to get your nod first. To make it open, \r\n    <a href=\"http://{{ domain }}{%% url group_edit content_object.group.slug %%}\">modify your team setting.</a>\r\n</p>\r\n<p>\r\n    Kudos,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: x.group.managers()',0,NULL,NULL,NULL,'2010-08-23 14:59:55','2010-08-23 14:59:55'),
	(10,'Team Membership Approved','Team Membership Response','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi {{ content_object.user.first_name }},<br />\r\n    You’re in! The team administrator just approved your access to {{ content_object.group }}.\r\n</p>\r\n<p>\r\n    Click here to go to <a href=\"http://{{ domain }}{{ content_object.group.get_absolute_url }}\">{{ content_object.group }}</a>\r\n</p>\r\n<p>\r\n    On the team page, you can see what your fellow members are doing and how you <br />\r\n    stack up. That means now is the time to get your upgrade on! If you’ve already <br />\r\n    checked off the <a href=\"http://{{ domain }}{%% url index %%}\">featured action</a>, \r\n    browse the <a href=\"http://{{ domain }}{%% url action_show %%}\">action library</a> to find \r\n    your next move.<br />\r\n    The more actions you do, the more points you and your new group will earn.\r\n</p>\r\n<p>\r\n    Cheers,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: x.user',0,NULL,NULL,NULL,'2010-08-23 13:32:27','2010-08-23 13:45:42'),
	(11,'Team Membership Denied','Team Membership Response','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi {{ content_object.user.first_name }},<br />\r\n    A team administrator turned down your request to join {{ content_object.group }}. But <br />\r\n    no worries! There are tons of teams on Repower at Home, including the one that’s <br />\r\n    perfect for you.\r\n</p>\r\n<p>\r\n    <a href=\"http://{{ domain }}{%% url group_list %%}\">Click here to find more teams</a><br />\r\n    <a href=\"http://{{ domain }}{%% url group_create %%}\">Click here to create your own</a>\r\n</p>\r\n<p>\r\n    Remember, only closed teams need administrator approval. You can join any  <br />\r\n    of the open teams at anytime.\r\n</p>\r\n<p>\r\n    Cheers,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: x.user',0,NULL,NULL,NULL,'2010-08-23 13:40:52','2010-08-23 15:05:42'),
	(12,'RSVP notification','RSVP from {{ content_object }} to {{ content_object.event }}','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi {{ content_object.event.creator.get_full_name }},\r\n    {{ content_object }} has rsvped to <a href=\"http://{{ domain }}{{ content_object.event.get_absolute_url }}\">{{ content_object.event }}</a>\r\n    as {{ content_object.get_rsvp_status_display }}.\r\n</p>\r\n<p>\r\n    Later,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'after_start',0,'lambda x: x.event.creator',0,NULL,NULL,NULL,'2010-08-25 12:25:09','2010-08-25 12:25:09'),
	(13,'Event reminder','Remember {{ content_object.event }} on {{ content_object.event.when|date }}','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hey {{ content_object.first_name }},\r\n</p>\r\n<p>\r\n    We’re really excited that you’re coming to a {{ content_object.event }}! Mark you calendar now:\r\n    <div style=\"margin-left: 15px;\">\r\n        <div>\r\n            <div style=\"float: left;\"><i>Location:</i></div>\r\n            <div style=\"margin-left: 60px;\">\r\n                {{ content_object.event.place_name }}<br />\r\n                {{ content_object.event.where }}<br />\r\n                {{ content_object.event.location }} {{ content_object.event.location.zipcode }}\r\n            </div>\r\n            <div style=\"clear: both;\"></div>\r\n        </div>\r\n        <div>\r\n            <div style=\"float: left;\"><i>Time:</i></div>\r\n            <div style=\"margin-left: 60px;\">\r\n                {{ content_object.event.start|time }}\r\n            </div>\r\n            <div style=\"clear: both;\"></div>\r\n        </div>\r\n        <div>\r\n            <div style=\"float: left;\"><i>Date:</i></div>\r\n            <div style=\"margin-left: 60px;\">\r\n                {{ content_object.event.when|date }}\r\n            </div>\r\n            <div style=\"clear: both;\"></div>\r\n        </div>\r\n    </div>\r\n</p>\r\n\r\n<p>\r\n    Here’s what {{ author.first_name }} wants you to know:<br />\r\n    {{ note }}\r\n</p>\r\n<p>\r\n    Thanks,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'send_immediately',0,'lambda x: x',0,NULL,NULL,NULL,'2010-08-26 10:41:09','2010-08-26 12:08:25'),
	(14,'Event announcement','Update about {{ content_object.event }} on {{ content_object.event.when|date }}','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi {{ content_object.first_name }},\r\n</p>\r\n<p>\r\n    {{ author.first_name }} has an update about the event:<br />\r\n    {{ note }}\r\n</p>\r\n<p>\r\n    Thanks,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'send_immediately',0,'lambda x: x',0,NULL,NULL,NULL,'2010-08-26 10:44:59','2010-08-26 11:32:26'),
	(15,'Invite','Invitation from {{ content_object.user.get_full_name }} to Repower at Home','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi, <br />\r\n    {{ content_object.user.get_full_name }} ({{ content_object.user.email }}) just invited you to join Repower at Home. <br />\r\n    Your challenge, if you choose to accept it, is to turn your home into a clean, mean, <br />\r\n    energy-saving machine. \r\n</p>\r\n<p>\r\n    <a href=\"{{ content_object.get_permalink }}\">Click here to accept the invitation</a>\r\n</p>\r\n{%% if note %%}\r\n    <p>\r\n       Here\'s what {{ content_object.user.first_name }} wants you to know: <br />\r\n       {{ note|safe }} \r\n    </p>\r\n{%% endif %%}\r\n<p>\r\n    Repower at Home is giving homes across the country a power boost. We’re the <br />\r\n    premiere online community for people who want to save energy, save money, and <br />\r\n    save the world. As a member, you can access awesome energy-saving tips, link up <br />\r\n    with groups in your ‘hood, and more.\r\n</p>\r\n<p>\r\n    Get started today – it’s free!\r\n</p>\r\n<p>\r\n    <a href=\"https://{{ domain }}{%% url register %%}\">Sign up at repowerathome.com</a>\r\n</p>\r\n<p>\r\n    Cheers,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'send_immediately',0,'lambda x: x.email',0,NULL,NULL,NULL,'2010-08-26 12:45:17','2010-08-26 13:03:01'),
	(16,'Event invite','Invitation from {{ content_object.user.get_full_name }} to Repower at Home','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi,<br />\r\n    {{ content_object.user.get_full_name }} wants you to attend a Repower at Home {{ content_object.content_object.event_type }} <br />\r\n    in {{ content_object.content_object.location }} on {{ content_object.content_object.when|date:\"LONG_DATE_FORMAT\" }} at \r\n    {{ content_object.content_object.start|time }}.\r\n</p>\r\n<p>\r\n    <a href=\"http://{{ domain }}{%% url event-invite content_object.content_object.id content_object.token %%}\">\r\n        Click here to RSVP\r\n    </a>\r\n</p>\r\n{%% if note %%}\r\n    <p>\r\n       Here\'s what {{ content_object.user.first_name }} wants you to know: <br />\r\n       {{ note|safe }} \r\n    </p>\r\n{%% endif %%}\r\n<p>\r\n    Later,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'send_immediately',0,'lambda x: x.email',0,NULL,NULL,NULL,'2010-08-26 12:50:44','2010-08-26 13:04:55'),
	(17,'Team invite','Invitation from {{ content_object.user.get_full_name }} to Repower at Home','{%% extends \'rah/base_email.html\' %%}\r\n{%% block email_content %%}\r\n<p>\r\n    Hi,<br />\r\n    {{ content_object.user.get_full_name}} wants you to join {{ content_object.content_object }}.\r\n</p>\r\n<p>\r\n    <a href=\"{{ content_object.get_permalink }}\">Click here to accept the invitation</a>\r\n</p>\r\n{%% if note %%}\r\n    <p>\r\n       Here\'s what {{ content_object.user.first_name }} wants you to know: <br />\r\n       {{ note|safe }} \r\n    </p>\r\n{%% endif %%}\r\n<p>\r\n    {{ content_object.content_object }} is a Repower at Home team. Repower at Home teams help<br />\r\n    members get organized, connect with friends, and save the world through the magic of<br /> \r\n    energy efficiency. Link up today – it’s free\r\n</p>\r\n<p>\r\n    <a href=\"https://{{ domain }}{%% url register %%}\">Join now</a>\r\n</p>\r\n<p>\r\n    Ciao,<br />\r\n    The Repower at Home team\r\n</p>\r\n{%% endblock %%}',0,'send_immediately',0,'lambda x: x.email',0,NULL,NULL,NULL,'2010-08-26 12:52:16','2010-08-26 13:03:50');
